  UpdateActor: 'UPDATE [DIAGRAMA].[dbo].[Actor] SET Nombre = @Nombre, Fecha_Nacimiento = @Fecha_Nacimiento, Pais_Nacimiento = @Pais_Nacimiento, Biografia = @Biografia, Foto_Actor = @Foto_Actor WHERE ID_Actor = @ID;',
